import React, { Fragment, useEffect, useState } from "react";
import { Autocomplete, CircularProgress, Grid, TextField } from "@mui/material";
import { IMovie } from "../interfaces/movieInterface";
import movieImage from "../images/movieIMG.jpeg";
import { Link, useHistory } from "react-router-dom";
import { height } from "@mui/system";
import { get, ALL_MOVIES } from "../services/restService";
import { formatWithOptions } from "util";

export const SearchBar = () => {
  const history = useHistory();
  const [suggestions, setSuggestions] = useState();
  const [open, setOpen] = useState(false);
  const [movieData, setMovieData] = useState<IMovie[]>([]);
  const loading = open && formatWithOptions.length === 0;

  useEffect(() => {
    let active = true;

    if (!loading) {
      return undefined;
    }

    const getMovies = async () => {
      const data = await get(ALL_MOVIES);
      if (active) {
        setMovieData(data === undefined ? [] : data);
      }
    };
    getMovies();
    return () => {
      active = false;
    };
  }, [loading]);

  useEffect(() => {
    if (!open) {
      setMovieData([]);
    }
  }, [open]);
  const handleSelect = (event: any, value: any) => {
    history.push({
      pathname: `/movie/${value.id}`,
      state: value,
    });
    //Route to movie page
  };

  return (
    <>
      <Autocomplete
        id="free-solo"
        sx={{ width: 600 }}
        onOpen={() => {
          setOpen(true);
        }}
        onClose={() => {
          setOpen(false);
        }}
        isOptionEqualToValue={(option, value) => option.title === value.title}
        getOptionLabel={(option) => option.title}
        options={movieData}
        open={open}
        renderInput={(params) => (
          <TextField
            {...params}
            label="Movie"
            InputProps={{
              ...params.InputProps,
              type: "search",
              endAdornment: (
                <Fragment>
                  {loading ? (
                    <CircularProgress color="inherit" size={20} />
                  ) : null}
                  {params.InputProps.endAdornment}
                </Fragment>
              ),
            }}
          />
        )}
        onChange={(event, value) => handleSelect(event, value)}
      />
    </>
  );
};
