import { IMovie } from "../../../interfaces/movieInterface";

import {
  Box,
  Card,
  CardContent,
  CardMedia,
  Grid,
  ListItem,
  ListItemButton,
  Typography,
} from "@mui/material";
import { Rating } from "../../MoviePage/components/Rating";
import { useHistory } from "react-router";
import defaultImg from "../../../images/avengers.jpeg";

export const MovieItem = (movie: IMovie) => {
  const history = useHistory();

  const handleClick = () => {
    history.push({ pathname: `/movie/${movie.id}` });
  };

  return (
    <>
      <Card
        sx={{
          display: "flex",
          width: "50%",
          height: "300px",
        }}
      >
        <CardMedia
          component="img"
          sx={{ width: "50px" }}
          image={movie.imageUrl}
          alt="Live from space album cover"
        />
        <Box sx={{ display: "flex", flexDirection: "column" }}>
          <CardContent sx={{ flex: "1 0 auto" }}>
            <Typography component="div" variant="h4">
              {movie.title}
            </Typography>
            <Typography
              component="div"
              variant="subtitle2"
              color="text.secondary"
            >
              {movie.genre}
            </Typography>
            <Typography
              variant="subtitle2"
              color="text.secondary"
              component="div"
            >
              {movie.director}
            </Typography>
            <Typography
              variant="subtitle2"
              color="text.secondary"
              component="div"
              sx={{ paddingTop: "20px" }}
            >
              {movie.description}
            </Typography>
          </CardContent>
          <Box sx={{ display: "flex", alignItems: "center", pl: 1, pb: 1 }}>
            <Rating rating={movie.rating} numberOfVotes={movie.numberOfVotes} />
          </Box>
        </Box>
      </Card>
      {/* <ListItem>
        <ListItemButton onClick={handleClick}>
          <Grid container direction="row">
            <Grid item xs={2}>
              <img src={movie.imageUrl ?? defaultImg} width="50px" alt="err" />
            </Grid>
            <Grid item xs={6}>
              <h3>{movie.title}</h3>
            </Grid>
            <Grid item xs={4}>
              <div>
                <Rating
                  rating={movie.rating}
                  numberOfVotes={movie.numberOfVotes}
                ></Rating>
              </div>
            </Grid>
          </Grid>
        </ListItemButton>
      </ListItem> */}
    </>
  );
};
