import React, { useEffect, useState } from "react";
import {
  BrowserRouter,
  Route,
  Switch,
  useLocation,
  useParams,
} from "react-router-dom";

import { IMovie } from "../../interfaces/movieInterface";
import { MovieInfo } from "./components/MovieInfo";
import { Rating } from "./components/Rating";
import { RateMovie } from "./components/RateMovie";
import { get, GET_MOVIE } from "../../services/restService";
import { Grid } from "@material-ui/core";

export const MoviePage = () => {
  const { movieId } = useParams<{ movieId: string }>();
  const [movie, setMovie] = useState<IMovie>();
  const [test, setTest] = useState(10);

  useEffect(() => {
    const getMovie = async () => {
      const data = await get(GET_MOVIE, movieId);

      setMovie(data === undefined ? undefined : data);
      console.log(movie);
    };
    getMovie();
  }, [movieId]);

  const handleRatingChange = (rating: number) => {
    if (movie != undefined) {
      setMovie({ ...movie, rating: rating });
    }
  };

  return (
    <>
      {movie != undefined && (
        <div style={{ margin: "2%" }}>
          <Grid container direction="row" justifyContent="center">
            <Grid item>
              <MovieInfo
                id={movie.id}
                title={movie.title}
                description={movie.description}
                rating={movie.rating}
                numberOfVotes={movie.numberOfVotes}
              ></MovieInfo>
            </Grid>
            <Grid
              direction="column"
              style={{ backgroundColor: "blue", marginLeft: "5%" }}
            >
              <Rating
                rating={movie.rating}
                numberOfVotes={movie.numberOfVotes}
              ></Rating>

              <RateMovie handleRatingChange={handleRatingChange}></RateMovie>
            </Grid>
          </Grid>
        </div>
      )}
    </>
  );
};
