import { Box, List, ListItem, MenuItem, Select } from "@mui/material";
import React, { useEffect, useState } from "react";
import { MovieList } from "./components/MovieList";
import { IMovie } from "../../interfaces/movieInterface";
import {
  ALL_MOVIES,
  deleteMovie,
  get,
  GET_TOP_RATED_MOVIES,
} from "../../services/restService";

export const AllMoviesPage = () => {
  const [movies, setMovies] = useState<IMovie[]>();
  const [test, setTest] = useState();
  const [genre, setGenre] = useState();
  useEffect(() => {
    const fetch = async () => {
      const data = await get(GET_TOP_RATED_MOVIES);
      setMovies(data);
    };
    fetch();
  }, []);

  const handleDelete = async (id: string) => {
    setMovies(movies!.filter((movie) => movie.id != id));
    await deleteMovie(id);
  };

  return (
    <Box
      sx={{ width: "100%", height: "100%" }}
      display="flex"
      flexDirection="column"
      justifyContent="center"
      alignItems="center"
      minHeight="100vh"
    >
      <Select
        labelId="demo-simple-select-label"
        id="demo-simple-select"
        value={genre}
        label="Genre"
        onChange={() => {
          console.log("hallo");
        }}
      >
        {genres.forEach((item: string) => (
          <MenuItem value={item}>{item}</MenuItem>
        ))}
      </Select>
      {movies != undefined && (
        <MovieList movies={movies} handleDelete={handleDelete}></MovieList>
      )}
    </Box>
  );
};

const genres = [
  "Drama",
  "Crime",
  "Action",
  "Adventure",
  "Biography",
  "History",
  "Sci-Fi",
  "Romance",
  "Western",
  "Fantasy",
  "Comedy",
  "Thriller",
  "Animation",
  "Family",
  "War",
  "Mystery",
  "Music",
  "Sport",
  "Horror",
  "Musical",
  "Film-Noir",
];
