import React, { Fragment, useEffect, useState } from "react";
import { Autocomplete, CircularProgress, Grid, TextField } from "@mui/material";
import { IMovie } from "../interfaces/movieInterface";
import movieImage from "../images/movieIMG.jpeg";
import { Link, useHistory } from "react-router-dom";
import { height } from "@mui/system";
import { get, ALL_MOVIES, MATCHING_MOVIES } from "../services/restService";
import { formatWithOptions } from "util";
import { resolve } from "dns";
import throttle from "lodash/throttle";

export const SearchBar = () => {
  const history = useHistory();
  const [value, setValue] = useState();
  const [open, setOpen] = useState(false);
  const [options, setOptions] = useState<readonly IMovie[]>([]);
  const [inputValue, setInputValue] = useState("");
  const loading = open && options.length === 0;

  useEffect(() => {
    let active = true;

    if (!loading) {
      return undefined;
    }
    console.log(inputValue.length);

    if (inputValue.length < 3) {
      // setOptions(value ? [value] : []);
      console.log("dsf");
      setOptions([]);
      return undefined;
    }

    const getMovies = async () => {
      const data = await get(MATCHING_MOVIES, inputValue);
      if (active) {
        setOptions(data === undefined ? [] : data);
      }
    };
    getMovies();
    return () => {
      active = false;
    };
  }, [value, inputValue]);

  useEffect(() => {
    if (!open) {
      setOptions([]);
    }
  }, [open]);
  const handleSelect = (event: any, value: any) => {
    if (value != null) {
      history.push({
        pathname: `/movie/${value.id}`,
        state: value,
      });
    }

    //Route to movie page
  };

  return (
    <>
      <Autocomplete
        id="free-solo"
        sx={{ width: 600 }}
        onOpen={() => {
          setOpen(true);
        }}
        onClose={() => {
          setOpen(false);
        }}
        value={value}
        isOptionEqualToValue={(option, value) => option.title === value.title}
        getOptionLabel={(option) => option.title}
        options={options}
        open={open}
        filterOptions={(x) => x}
        //onChange={(event, value) => handleSelect(event, value)}
        onInputChange={(event, newInputValue) => {
          setInputValue(newInputValue);
        }}
        renderInput={(params) => (
          <TextField
            {...params}
            label="Movie"
            InputProps={{
              ...params.InputProps,

              endAdornment: (
                <Fragment>
                  {loading ? (
                    <CircularProgress color="inherit" size={20} />
                  ) : null}
                  {params.InputProps.endAdornment}
                </Fragment>
              ),
            }}
          />
        )}
      />
    </>
  );
};
