import { Card, CardMedia, Grid } from "@mui/material";
import { makeStyles } from "@mui/styles";
import React from "react";
import { IMovie } from "../../../interfaces/movieInterface";

export const CarouselItem = (movie: IMovie) => {
  const classes = useStyles();
  return (
    // <div className={classes.outterContainer}>
    //   <img src={movie.imageUrl} className={classes.img}></img>
    //   <div className={classes.innerContainer}>
    //     <h3>asdfasf</h3>
    //   </div>
    // </div>
    <Card className={classes.card}>
      <CardMedia image={movie.imageUrl} className={classes.media} />
      <div className={classes.overlay}>this text should overlay the image</div>
    </Card>
  );
};

const useStyles = makeStyles((theme) => ({
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  card: {
    position: "relative",
    height: "100%",
  },
  overlay: {
    position: "absolute",
    top: "20px",
    left: "20px",
    color: "black",
    backgroundColor: "white",
  },
}));
