import { IMovie } from "../../../interfaces/movieInterface";

import { Button, makeStyles } from "@material-ui/core";
import { Divider, Grid, ListItem, ListItemButton } from "@mui/material";
import { Rating } from "../../MoviePage/components/Rating";
import { useHistory } from "react-router";

export const MovieItem = (props: { movie: IMovie; index?: number }) => {
  const history = useHistory();

  const handleClick = () => {
    history.push({ pathname: `/movie/${props.movie.id}` });
  };
  const classes = useStyles();
  return (
    <ListItem sx={{ width: "100%" }}>
      <ListItemButton onClick={handleClick}>
        {/* {props.index != undefined && (
          <Grid container justifyContent="flex-start">
            <h3>{props.index}</h3>
          </Grid>
        )} */}
        <Grid container direction="column" justifyContent="flex-start">
          <h3>{props.movie.title}</h3>
        </Grid>
        <Grid container justifyContent="flex-end">
          <div>
            <Rating
              rating={props.movie.rating}
              numberOfVotes={props.movie.numberOfVotes}
            ></Rating>
          </div>
        </Grid>
      </ListItemButton>
      <Divider className={classes.divider} />
    </ListItem>
  );
};
const useStyles = makeStyles({
  root: {
    backgroundColor: "blue",
    "&:hover": {
      backgroundColor: "white",
      color: "red",
    },
  },
  divider: {
    // Theme Color, or use css color in quote
    background: "white",
  },
});
