        </CardContent>
        <Box sx={{ display: "flex", alignItems: "center", pl: 1, pb: 1 }}>
          <Rating rating={movie.rating} numberOfVotes={movie.numberOfVotes} />
        </Box>
      </Box>
    </Card>
  );
};
