import { List } from "@mui/material";
import React from "react";
import { IMovie } from "../../../interfaces/movieInterface";
import { MovieItem } from "./MovieItem";

export const ScrollList = (props: { movies: IMovie[] }) => {
  const checkOnBottom = (event: any) => {
    console.log("sdf");

    if (event.target.scrollTop === event.target.scrollHeight) {
      console.log("NU ÄR VI I BOTTEN");
    }
  };

  return (
    <div onScroll={checkOnBottom}>
      <List sx={{ height: "100%", width: "100%", overflowY: "scroll" }}>
        {props.movies.map((movie) => (
          <MovieItem
            id={movie.id}
            title={movie.title}
            description={movie.description}
            rating={movie.rating}
            numberOfVotes={movie.numberOfVotes}
          />
        ))}
      </List>
    </div>
  );
};
