import React from "react";
import { Home as HomeIcon } from "@material-ui/icons";
import {
  Drawer,
  Grid,
  Icon,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  makeStyles,
} from "@material-ui/core";
import { HomePage } from "./HomePage";
import { Link } from "react-router-dom";

const useStyles = makeStyles({
  list: { width: 250 },
});
type MenuItem = {
  icon?: React.ReactElement;
  name: string;
  route: string;
};

type Props = {
  open: boolean;
  toggleOpen: () => void;
};

const menuItem: MenuItem[] = [
  {
    name: "Home",
    route: "/",

    icon: <HomeIcon />,
  },
];
export const MenuDrawer = ({ open, toggleOpen }: Props) => {
  return (
    <Grid container>
      <Grid item>
        <Drawer open={open} onClose={toggleOpen}>
          <List>
            {menuItem.map((item) => (
              <ListItem component={Link} to={item.route}>
                <ListItemIcon>
                  <Icon>{item.icon}</Icon>
                </ListItemIcon>
                <ListItemText primary={item.name} />
              </ListItem>
            ))}
          </List>
        </Drawer>
      </Grid>
    </Grid>
  );
};
