    //   if (active) {
    //     setoptions(data === undefined ? [] : data);
    //   }
    // };
    // getMovies();
    // return () => {
    //   active = false;
    // };
  }, [loading]);

  useEffect(() => {
    if (!open) {
      setoptions([]);
    }
  }, [open]);
  const handleSelect = (event: any, value: any) => {
    if (value != null) {
      history.push({
        pathname: `/movie/${value.id}`,
        state: value,
      });
    }

    //Route to movie page
  };

  return (
    <>
      <Autocomplete
        id="free-solo"
        sx={{ width: 600 }}
        onOpen={() => {
          setOpen(true);
        }}
        onClose={() => {
          setOpen(false);
        }}
        isOptionEqualToValue={(option, value) => option.title === value.title}
        getOptionLabel={(option) => option.title}
        options={options}
        open={open}
        renderInput={(params) => (
          <TextField
            {...params}
            label="Movie"
            InputProps={{
              ...params.InputProps,

              endAdornment: (
                <Fragment>
                  {loading ? (
                    <CircularProgress color="inherit" size={20} />
                  ) : null}
                  {params.InputProps.endAdornment}
                </Fragment>
              ),
            }}
          />
        )}
        onChange={(event, value) => handleSelect(event, value)}
      />
    </>
  );
};
