import { IMovie } from "../../../interfaces/movieInterface";

import { Button, makeStyles } from "@material-ui/core";
import { Grid, ListItem, ListItemButton } from "@mui/material";
import { Rating } from "../../MoviePage/components/Rating";
import { useHistory } from "react-router";

export const MovieItem = (movie: IMovie) => {
  const history = useHistory();
  const handleClick = () => {
    history.push({ pathname: `/movie/${movie.id}` });
  };
  const classes = useStyles();
  return (
    <ListItemButton onClick={handleClick} sx={{ width: "100%" }}>
      <Grid container direction="column" justifyContent="flex-start">
        <h3>{movie.title}</h3>
      </Grid>
      <Grid container justifyContent="flex-end">
        <div>
          <Rating
            rating={movie.rating}
            numberOfVotes={movie.numberOfVotes}
          ></Rating>
        </div>
      </Grid>
    </ListItemButton>
  );
};
const useStyles = makeStyles({
  root: {
    backgroundColor: "blue",
    "&:hover": {
      backgroundColor: "white",
      color: "red",
    },
  },
});
